<svelte:options immutable={true} />

<script>import { faCaretSquareDown, faCaretSquareUp, faClone, faCopy, faCropAlt, faCut, faFilter, faPaste, faPen, faSortAmountDownAlt, faTimes } from '@fortawesome/free-solid-svg-icons';
import { getIn } from 'immutable-json-patch';
import { initial, isEmpty } from 'lodash-es';
import { onMount } from 'svelte';
import Icon from 'svelte-awesome';
import DropdownButton from '../../../controls/DropdownButton.svelte';
import { SELECTION_TYPE } from '../../../../logic/selection';
import { keyComboFromEvent } from '../../../../utils/keyBindings';
import { isObjectOrArray } from '../../../../utils/typeUtils';
import { faCheckSquare, faLightbulb, faSquare } from '@fortawesome/free-regular-svg-icons';
import { STATE_ENFORCE_STRING } from '../../../../constants';
import { isObject } from '../../../../utils/typeUtils';
import { canConvert, singleItemSelected } from '../../../../logic/selection';
import { findNearestElement } from '../../../../utils/domUtils';
export let json;
export let state;
export let selection;
export let showTip;
export let onCloseContextMenu;
export let onEditKey;
export let onEditValue;
export let onToggleEnforceString;
export let onCut;
export let onCopy;
export let onPaste;
export let onRemove;
export let onDuplicate;
export let onExtract;
export let onInsertBefore;
export let onInsert;
export let onConvert;
export let onInsertAfter;
export let onSort;
export let onTransform;
let refContextMenu;
onMount(() => {
    setTimeout(() => {
        const firstEnabledButton = [...refContextMenu.querySelectorAll('button')].find((button) => !button.disabled);
        if (firstEnabledButton) {
            firstEnabledButton.focus();
        }
    });
});
$: hasJson = json !== undefined;
$: hasSelection = selection != null;
$: rootSelected = hasSelection && isEmpty(selection.focusPath);
$: focusValue = hasSelection ? getIn(json, selection.focusPath) : undefined;
$: hasSelectionContents =
    hasJson &&
        selection != null &&
        (selection.type === SELECTION_TYPE.MULTI ||
            selection.type === SELECTION_TYPE.KEY ||
            selection.type === SELECTION_TYPE.VALUE);
$: canDuplicate = hasJson && hasSelectionContents && !rootSelected; // must not be root
$: canExtract =
    hasJson &&
        selection != null &&
        (selection.type === SELECTION_TYPE.MULTI || selection.type === SELECTION_TYPE.VALUE) &&
        !rootSelected; // must not be root
$: canEditKey =
    hasJson &&
        selection != null &&
        singleItemSelected(selection) &&
        !rootSelected &&
        !Array.isArray(getIn(json, initial(selection.focusPath)));
$: canEditValue =
    hasJson && selection != null && singleItemSelected(selection) && !isObjectOrArray(focusValue);
$: convertMode = hasSelectionContents;
$: insertOrConvertText = convertMode ? 'Convert to' : 'Insert';
$: canInsertOrConvertStructure = convertMode ? false : hasSelection;
$: canInsertOrConvertObject = convertMode
    ? canConvert(selection) && !isObject(focusValue)
    : hasSelection;
$: canInsertOrConvertArray = convertMode
    ? canConvert(selection) && !Array.isArray(focusValue)
    : hasSelection;
$: canInsertOrConvertValue = convertMode
    ? canConvert(selection) && isObjectOrArray(focusValue)
    : hasSelection;
$: enforceString =
    selection != null
        ? getIn(state, selection.focusPath.concat(STATE_ENFORCE_STRING)) === true
        : false;
function handleEditKey() {
    onCloseContextMenu();
    onEditKey();
}
function handleEditValue() {
    onCloseContextMenu();
    onEditValue();
}
function handleToggleEnforceString() {
    onCloseContextMenu();
    onToggleEnforceString();
}
function handleCut() {
    onCloseContextMenu();
    onCut(true);
}
function handleCutCompact() {
    onCloseContextMenu();
    onCut(false);
}
function handleCopy() {
    onCloseContextMenu();
    onCopy(true);
}
function handleCopyCompact() {
    onCloseContextMenu();
    onCopy(false);
}
function handlePaste() {
    onCloseContextMenu();
    onPaste();
}
function handleRemove() {
    onCloseContextMenu();
    onRemove();
}
function handleDuplicate() {
    onCloseContextMenu();
    onDuplicate();
}
function handleExtract() {
    onCloseContextMenu();
    onExtract();
}
function handleInsertOrConvert(type) {
    onCloseContextMenu();
    if (hasSelectionContents) {
        onConvert(type);
    }
    else {
        onInsert(type);
    }
}
function handleSort() {
    onCloseContextMenu();
    onSort();
}
function handleTransform() {
    onCloseContextMenu();
    onTransform();
}
function handleInsertBefore() {
    onCloseContextMenu();
    onInsertBefore();
}
function handleInsertAfter() {
    onCloseContextMenu();
    onInsertAfter();
}
function handleKeyDown(event) {
    const combo = keyComboFromEvent(event).replace(/^Command\+/, 'Ctrl+');
    if (combo === 'Up' || combo === 'Down' || combo === 'Left' || combo === 'Right') {
        event.preventDefault();
        const buttons = Array.from(refContextMenu.querySelectorAll('button:not([disabled])'));
        const nearest = findNearestElement({
            allElements: buttons,
            currentElement: event.target,
            direction: combo,
            hasPrio: (element) => {
                return element.getAttribute('data-type') !== 'jse-open-dropdown';
            }
        });
        if (nearest) {
            nearest.focus();
        }
    }
}
$: editValueDropdownItems = [
    {
        icon: faPen,
        text: 'Edit value',
        title: 'Edit the value (Double-click on the value)',
        onClick: handleEditValue,
        disabled: !canEditValue
    },
    {
        icon: enforceString ? faCheckSquare : faSquare,
        text: 'Enforce string',
        title: 'Enforce keeping the value as string when it contains a numeric value',
        onClick: handleToggleEnforceString,
        disabled: !canEditValue
    }
];
$: cutDropdownItems = [
    {
        icon: faCut,
        text: 'Cut formatted',
        title: 'Cut selected contents, formatted with indentation (Ctrl+X)',
        onClick: handleCut,
        disabled: !hasSelectionContents
    },
    {
        icon: faCut,
        text: 'Cut compacted',
        title: 'Cut selected contents, without indentation (Ctrl+Shift+X)',
        onClick: handleCutCompact,
        disabled: !hasSelectionContents
    }
];
$: copyDropdownItems = [
    {
        icon: faCopy,
        text: 'Copy formatted',
        title: 'Copy selected contents, formatted with indentation (Ctrl+C)',
        onClick: handleCopy,
        disabled: !hasSelectionContents
    },
    {
        icon: faCopy,
        text: 'Copy compacted',
        title: 'Copy selected contents, without indentation (Ctrl+Shift+C)',
        onClick: handleCopyCompact,
        disabled: !hasSelectionContents
    }
];
</script>

<div class="jse-contextmenu" bind:this={refContextMenu} on:keydown={handleKeyDown}>
  <div class="jse-row">
    <button
      type="button"
      title="Edit the key (Double-click on the key)"
      on:click={handleEditKey}
      disabled={!canEditKey}
    >
      <Icon data={faPen} /> Edit key
    </button>
    <DropdownButton width="11em" items={editValueDropdownItems}>
      <button
        type="button"
        slot="defaultItem"
        title="Edit the value (Double-click on the value)"
        on:click={handleEditValue}
        disabled={!canEditValue}
      >
        <Icon data={faPen} /> Edit value
      </button>
    </DropdownButton>
  </div>
  <div class="jse-separator" />
  <div class="jse-row">
    <DropdownButton width="10em" items={cutDropdownItems}>
      <button
        type="button"
        slot="defaultItem"
        title="Cut selected contents, formatted with indentation (Ctrl+X)"
        on:click={handleCut}
        disabled={!hasSelectionContents}
      >
        <Icon data={faCut} /> Cut
      </button>
    </DropdownButton>
    <DropdownButton width="12em" items={copyDropdownItems}>
      <button
        type="button"
        slot="defaultItem"
        title="Copy selected contents, formatted with indentation (Ctrl+C)"
        on:click={handleCopy}
        disabled={!hasSelectionContents}
      >
        <Icon data={faCopy} /> Copy
      </button>
    </DropdownButton>
    <button
      type="button"
      title="Paste clipboard contents (Ctrl+V)"
      on:click={handlePaste}
      disabled={!hasSelection}
    >
      <Icon data={faPaste} /> Paste
    </button>
  </div>
  <div class="jse-separator" />
  <div class="jse-row">
    <div class="jse-column">
      <button
        type="button"
        title="Remove selected contents (Delete)"
        on:click={handleRemove}
        disabled={!hasSelectionContents}
      >
        <Icon data={faTimes} /> Remove
      </button>
      <button
        type="button"
        title="Duplicate selected contents (Ctrl+D)"
        on:click={handleDuplicate}
        disabled={!canDuplicate}
      >
        <Icon data={faClone} /> Duplicate
      </button>
      <button
        type="button"
        title="Extract selected contents"
        on:click={handleExtract}
        disabled={!canExtract}
      >
        <Icon data={faCropAlt} /> Extract
      </button>
      <button
        type="button"
        title="Sort array or object contents"
        on:click={handleSort}
        disabled={!hasSelectionContents}
      >
        <Icon data={faSortAmountDownAlt} /> Sort
      </button>
      <button
        type="button"
        title="Transform array or object contents (filter, sort, project)"
        on:click={handleTransform}
        disabled={!hasSelectionContents}
      >
        <Icon data={faFilter} /> Transform
      </button>
    </div>
    <div class="jse-column">
      <div class="jse-label">
        {insertOrConvertText}:
      </div>
      <button
        type="button"
        on:click={() => handleInsertOrConvert('structure')}
        title="{insertOrConvertText} structure"
        disabled={!canInsertOrConvertStructure}
      >
        <span class="jse-insert"><span class="jse-plus">{'+'}</span></span> Structure
      </button>
      <button
        type="button"
        on:click={() => handleInsertOrConvert('object')}
        title="{insertOrConvertText} object"
        disabled={!canInsertOrConvertObject}
      >
        <span class="jse-insert">{'{}'}</span> Object
      </button>
      <button
        type="button"
        on:click={() => handleInsertOrConvert('array')}
        title="{insertOrConvertText} array"
        disabled={!canInsertOrConvertArray}
      >
        <span class="jse-insert">[]</span> Array
      </button>
      <button
        type="button"
        on:click={() => handleInsertOrConvert('value')}
        title="{insertOrConvertText} value"
        disabled={!canInsertOrConvertValue}
      >
        <span class="jse-insert"><span class="jse-quote">"</span></span> Value
      </button>
    </div>
  </div>
  <div class="jse-separator" />
  <div class="jse-row">
    <button
      type="button"
      title="Select area before current entry to insert or paste contents"
      disabled={!hasSelectionContents || rootSelected}
      on:click={handleInsertBefore}
    >
      <Icon data={faCaretSquareUp} /> Insert before
    </button>
    <button
      type="button"
      title="Select area after current entry to insert or paste contents"
      disabled={!hasSelectionContents || rootSelected}
      on:click={handleInsertAfter}
    >
      <Icon data={faCaretSquareDown} /> Insert after
    </button>
  </div>
  {#if showTip}
    <div class="jse-row">
      <div class="jse-tip">
        <div>
          <Icon data={faLightbulb} />
        </div>
        <div>Tip: you can open this context menu via right-click or with Ctrl+Q</div>
      </div>
    </div>
  {/if}
</div>

<style src="./ContextMenu.scss">.jse-contextmenu {
  box-shadow: var(--jse-controls-box-shadow);
  font-family: var(--jse-font-family);
  font-size: var(--jse-font-size);
  background: var(--jse-context-menu-background);
  color: var(--jse-context-menu-color);
}
.jse-contextmenu button {
  border: none;
  background: transparent;
  color: inherit;
  cursor: pointer;
  font-family: var(--jse-font-family);
  font-size: var(--jse-font-size);
  padding: 5px;
  margin: 0;
}
.jse-contextmenu button:focus {
  background: var(--jse-context-menu-background-highlight);
}
.jse-contextmenu .jse-row {
  display: flex;
  flex-direction: row;
  align-items: flex-start;
  justify-content: stretch;
}
.jse-contextmenu .jse-row div.jse-label {
  flex: 1;
  white-space: nowrap;
  padding: var(--jse-padding);
  color: var(--jse-context-menu-color-disabled);
  line-height: normal;
}
.jse-contextmenu .jse-row div.jse-tip {
  flex: 1;
  background: var(--jse-context-menu-tip-background);
  color: var(--jse-context-menu-tip-color);
  margin: calc(0.5 * var(--jse-padding));
  padding: calc(0.5 * var(--jse-padding)) var(--jse-padding);
  font-size: 80%;
  line-height: 1.3em;
  display: flex;
  flex-direction: row;
  align-items: center;
  gap: var(--jse-padding);
  border-radius: 3px;
}
.jse-contextmenu .jse-row div.jse-tip div {
  vertical-align: middle;
}
.jse-contextmenu .jse-row button {
  flex: 1;
  white-space: nowrap;
  padding: var(--jse-padding);
  color: inherit;
}
.jse-contextmenu .jse-row button:hover {
  background: var(--jse-context-menu-background-highlight);
}
.jse-contextmenu .jse-row button:disabled {
  color: var(--jse-context-menu-color-disabled);
  background: unset;
}
.jse-contextmenu .jse-row button .jse-insert {
  display: inline-block;
  position: relative;
  width: 18px;
  height: 18px;
  font-family: var(--jse-font-family-mono);
  font-weight: bold;
  font-size: 16px;
}
.jse-contextmenu .jse-row button .jse-insert .jse-quote,
.jse-contextmenu .jse-row button .jse-insert .jse-plus {
  position: absolute;
  top: -4px;
  left: 2px;
  font-size: 24px;
}
.jse-contextmenu .jse-column {
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: stretch;
}
.jse-contextmenu .jse-column:not(:last-child) {
  border-right: 1px solid var(--jse-context-menu-separator-color);
}
.jse-contextmenu .jse-column button {
  text-align: left;
  min-width: 130px;
}
.jse-contextmenu .jse-separator {
  width: 100%;
  height: 1px;
  background: var(--jse-context-menu-separator-color);
}</style>
