/**
 * @typedef {*} HistoryItem
 * @property {Object} undo
 * @property {Object} redo
 */
export interface HistoryOptions {
    maxItems?: number;
    onChange?: (props: {
        canUndo: boolean;
        canRedo: boolean;
        length: number;
    }) => void;
}
export declare function createHistory(options?: HistoryOptions): {
    add: (item: any) => void;
    clear: () => void;
    getState: () => {
        canUndo: boolean;
        canRedo: boolean;
        length: number;
    };
    undo: () => any;
    redo: () => any;
};
